package u6;

public class CharactersAreNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c = 'A';
		
		System.out.println(c);
		System.out.println((int)c);
		
		c++;
		System.out.println(c);
		System.out.println((int)c);
		
		c+=10;
		System.out.println(c);
		System.out.println((int)c);
		
		c+=20;
		System.out.println(c);
		System.out.println((int)c);
	}

}
