package u6;

public class WhileSquared {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
			
		while (i<=7){
			System.out.println(i + " squared is " + (i*i));
			i++;
		}
		for (int j = 0; j <= 7; j++){
			System.out.println(j + " squared is " + (j*j));
		}
	}

}
